import React, { Component } from 'react';

//creact mode 선택 시 나타나는 컴포넌트
class UpdateContent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: this.props.data.title,
      desc: this.props.data.desc,
    };
  }
  inputFoemHandler(e) {
    this.setState({ title: e.target.title });
  }
  render() {
    return (
      <article>
        <h2>Update</h2>
        <form
          action="/create_process"
          method="post"
          onSubmit={function (e) {
            e.preventDefault();
            this.props.onSubmit(e.target.title.value, e.target.desc.value);
          }.bind(this)}>
          <p>
            <input type="text" name="title" placeholder="title" value={this.state.title} onChange={this.inputFoemHandler().bind(this)} />
          </p>
          <p>
            <textarea
              name="desc"
              placeholder="description"
              value={this.state.desc}
              onChange={function (e) {
                this.setState({ desc: e.target.value });
              }.bind(this)}></textarea>
          </p>
          <p>
            <input type="submit" />
          </p>
        </form>
      </article>
    );
  }
}
export default UpdateContent;
